function deC(code) {
	var i = 0;
	var store = 0;
	var n = code.length;
	while (i < n) {
		var string = "";
		if ((code.charAt(i) == "/") && (code.charAt(i + 1) == "/")) {
			store = i;
			while (code.charAt(i) !== "\n") {
				string = string + code.charAt(i);
				i++;
			}
			i = store + 1;
		}
		i++;
		code = code.replace(string, "");
		if (code.includes("//") === false) {
			break;
		}
	}
	i = 0;
	store = 0;
	while (i < n) {
		var string = "";
		if ((code.charAt(i) == "/") && (code.charAt(i + 1) == "*")) {
			store = i;
			while (!((code.charAt(i) == "*") && (code.charAt(i + 1) == "/"))) {
				string = string + code.charAt(i);
				i++;
			}
			string = string + code.charAt(i) + code.charAt(i+1);
			i = store + 1;
		}
		i++;
		code = code.replace(string, "");
		if (code.includes("/*") === false) {
			break;
		}
	}
	return code;
}

function reB(code) {
	var i = 0;
	var string = "";
	while (code.charAt(i) != "{") {
		string = string + code.charAt(i);
		i++;
	}
	code = code.replace(string, "");
  code = code.replace(/(\r\n|\n|\r)/gm, "");
  code = code.replace(/\s/g, "");
  return code;
}

function attempt(code1, code2) {
	if (reB(deC(code1)) === reB(deC(code2))) {
		return false;
	}
	return true;
}

module.exports = {deC : deC, reB : reB};
